mapper.update:
UPDATE STOCK_OPER_APPLY_INFO a
WHEN a.id = #{item.id} THEN 1
WHEN a.id = #{item.id} THEN #{item.requireBy}
WHERE a.STOCK_STATUS = 0 AND a.note is null AND a.id IN
#{item.id}

MERGE INTO (select * from STOCK_OPER_APPLY_INFO where ALL_COMIT = 0 and note is null and over_time_not =0) a using (SELECT b.platform, b.material_code, b.Team, b.area, count(1) statusCount, sum(b.STOCK_STATUS) statusSum FROM STOCK_OPER_APPLY_INFO b WHERE b.ALL_COMIT = 0 AND b.note is null and b.over_time_not =0 AND b.STOCK_STATUS IN (0, 1) AND b.platform || b.material_code || b.Team || b.AREA IN (
#{item}
) GROUP BY b.platform, b.material_code, b.Team, b.area) c ON (a.platform = c.platform AND a.material_code = c.material_code AND a.Team = c.Team AND a.area = c.area and c.statusCount = c.statusSum ) WHEN MATCHED THEN UPDATE SET a.ALL_COMIT = 1

update STOCK_OPER_APPLY_INFO t

    t.STOCK_STATUS=2,



                WHEN t.platform || t.material_code||t.team||t.area = #{item.platformMaterialCodeTeamArea} THEN #{item.teamVerifId}




where t.all_comit=1 and t.OVER_TIME_NOT!=1 and STOCK_STATUS=1 and (note is null or note like '%建议淘汰产品%')

    and t.platform || t.material_code||t.team||t.area IN

        #{code}

