mapper.update:
UPDATE
STOCK_OPER_APPLY_INFO a



            WHEN a.id = #{item.id} THEN 1





                WHEN a.id = #{item.id} THEN #{item.applyPersonNo}






                WHEN a.id = #{item.id} THEN #{item.applyPersonName}







                WHEN a.id = #{item.id} THEN #{item.turnoverDaysArea}







                WHEN a.id = #{item.id} THEN #{item.stockQtyArea}







                WHEN a.id = #{item.id} THEN #{item.operCoversSalesDate}







                WHEN a.id = #{item.id} THEN #{item.salesDemand}







                WHEN a.id = #{item.id} THEN #{item.extraDays}







                WHEN a.id = #{item.id} THEN #{item.stockReason}







                WHEN a.id = #{item.id} THEN #{item.operExpectedDate}







                WHEN a.id = #{item.id} THEN #{item.salesStockDays}







                WHEN a.id = #{item.id} THEN #{item.operCurMonthQty}








                WHEN a.id = #{item.id} THEN #{item.operCurMonthAdd1Qty}







                WHEN a.id = #{item.id} THEN #{item.operCurMonthAdd2Qty}







                WHEN a.id = #{item.id} THEN #{item.operCurMonthAdd3Qty}







                WHEN a.id = #{item.id} THEN #{item.operCurMonthAdd4Qty}







                WHEN a.id = #{item.id} THEN #{item.operCurMonthAdd5Qty}







                WHEN a.id = #{item.id} THEN #{item.operCurMonthAdd6Qty}







                WHEN a.id = #{item.id} THEN #{item.applyDate}






            WHEN a.id = #{item.id} THEN SYSDATE



WHERE a.STOCK_STATUS = 0 AND a.note is null
AND a.id IN

    #{item.id}

