SELECT
    a.MATERIAL_CODE,
    MAX(a.DEPARTMENT) AS DEPARTMENT,
    MAX(a.TEAM) AS TEAM,
    MAX(a.BIZDATE) AS BIZDATE,
    MAX(material.PRODUCT_TYPE) AS PRODUCT_TYPE,
    MAX(material.PRODUCT_NAME) AS PRODUCT_NAME ,
    MAX(material.STYLE) AS  STYLE ,
    MAX(material.MAIN_MATERIAL)  AS MAIN_MATERIAL,
    MAX(material.DESIGN) AS PATTERN,
    MAX(material.COMPANY_BRAND) AS COMPANY_BRAND,
    MAX(material.FIT_BRAND) AS BRAND,
    MAX(material.MODEL)  AS MODEL,
    MAX(material.COLOR) AS COLOR,
    MAX(material.SIZES) AS SIZES,
    MAX(material.PACKING) AS PACKING,
    MAX(material.VERSION_DES) AS VERSION_DES,
    MAX(material.VERSION) AS VERSION,
    COUNT(a.ASIN) AS ASIN_COUNT ,
    SUM(a.RECOM_PRE_QTY) AS RECOM_PRE_QTY_SUM,-- 建议备货数量合计算  SALES_DEMAND
    SUM(a.SALES_DEMAND) AS SALES_DEMAND_SUM,-- 申请备货数量合计算  ,
    SUM(a.STOCK_QTY_AREA) AS STOCK_QTY_AREA_SUM,-- 申请区域备货数量合计算  ,
    LISTAGG(a.id,',') WITHIN GROUP(ORDER BY a.id) AS ids
FROM (SELECT product_type, platform, team, area, operator
        FROM product_line2
GROUP BY product_type, platform, team, area, operator) l
JOIN  STOCKING.STOCK_OPER_APPLY_INFO a
    ON l.platform = a.platform
    AND l.team = a.Team
    AND l.area = a.area
    AND l.product_type = a.product_type
JOIN MCMS_BASE_DATA.RP_MATERIAL material
ON a.MATERIAL_CODE = material.MATERIAL_CODE
WHERE
a.material_code IS NOT NULL
AND OVER_TIME_NOT = 0
and (a.note is null or a.note like '%建议淘汰产品%')


        AND l.operator like '%'||#{reqVO.operator}||'%'



        AND a.stock_status = #{reqVO.stockStatus}


        AND a.SALES_DEMAND >= #{reqVO.salesRequireLow}


        AND a.SALES_DEMAND < #{reqVO.salesRequireHigh}


        AND a.STOCK_QTY_AREA >= #{reqVO.stockQtyAreaLow}


        AND a.STOCK_QTY_AREA < #{reqVO.stockQtyAreaHeight}


        AND a.TEAM_VERIF_NO = #{reqVO.teamVerifNo}


        AND a.TEAM_VERIF_NO IN

            #{teamVerifNo}




        AND a.area IN

            #{area}





        AND a.material_code = #{reqVO.materialcode}


        AND a.material_code IN

            #{code}




        AND material.category IN

            #{category}




        AND material.product_type = #{reqVO.productType}


        AND material.product_type IN

            #{productType}





        AND material.product_name = #{reqVO.productname}


        AND material.product_name IN

            #{productname}




        AND material.style = #{reqVO.style}


        AND material.style IN

            #{style}






        AND material.main_material = #{reqVO.mainmaterial}


        AND material.main_material IN

            #{mainmaterial}





        AND material.DESIGN = #{reqVO.design}


        AND material.DESIGN IN

            #{design}





        AND material.FIT_BRAND = #{reqVO.brand}


        AND material.FIT_BRAND IN

            #{brand}





        AND material.company_brand IN

            #{companybrand}





        AND material.model = #{reqVO.model}


        AND material.model IN

            #{model}






        AND material.color = #{reqVO.color}


        AND material.color IN

            #{color}





        AND material.sizes = #{reqVO.sizes}


        AND material.sizes IN

            #{sizes}





        AND material.packing = #{reqVO.packing}


        AND material.packing IN

            #{packing}





        AND material.version = #{reqVO.version}


        AND material.version IN

            #{version}




        AND material.MATSTYLESECONDLABEL IN

            #{styleLabel}



        AND a.Team IN

            #{Team}







         ${reqVO.dataScopeMaterial}



        ${reqVO.dataScopeArea}


GROUP BY a.MATERIAL_CODE
ORDER BY a.MATERIAL_CODE

mapper.update:
MERGE INTO (select * from STOCK_OPER_APPLY_INFO where ALL_COMIT = 0 and (note is null or note like '%建议淘汰产品%') and over_time_not =0) a using (SELECT b.platform, b.material_code, b.Team, b.area, count(1) statusCount, sum(b.STOCK_STATUS) statusSum FROM STOCK_OPER_APPLY_INFO b WHERE b.ALL_COMIT = 0 AND (b.note is null or b.note like '%建议淘汰产品%') and b.over_time_not =0 AND b.STOCK_STATUS IN (0, 1) AND b.platform || b.material_code || b.Team || b.AREA IN (
#{item}
) GROUP BY b.platform, b.material_code, b.Team, b.area) c ON (a.platform = c.platform AND a.material_code = c.material_code AND a.Team = c.Team AND a.area = c.area and c.statusCount = c.statusSum ) WHEN MATCHED THEN UPDATE SET a.ALL_COMIT = 1

mapper.select:
SELECT a.MATERIAL_CODE, a.PLATFORM, a.AREA, MAX(a.BIZDATE) AS BIZDATE, MAX(material.PRODUCT_TYPE) AS PRODUCT_TYPE, MAX(material.PRODUCT_NAME) AS PRODUCT_NAME , MAX(material.STYLE) AS STYLE , MAX(material.MAIN_MATERIAL) AS MAIN_MATERIAL, MAX(material.DESIGN) AS PATTERN, MAX(material.COMPANY_BRAND) AS COMPANY_BRAND, MAX(material.FIT_BRAND) AS BRAND, MAX(material.MODEL) AS MODEL, MAX(material.COLOR) AS COLOR, MAX(material.SIZES) AS SIZES, MAX(material.PACKING) AS PACKING, MAX(material.VERSION_DES) AS VERSION_DES, MAX(material.VERSION) AS VERSION, COUNT(a.ASIN) AS ASIN_COUNT , SUM(a.RECOM_PRE_QTY) AS RECOM_PRE_QTY_SUM,-- 建议备货数量合计算 SUM(a.SALES_DEMAND) AS SALES_DEMAND_SUM,-- 申请备货数量合计算 , SUM(a.STOCK_QTY_AREA) AS STOCK_QTY_AREA_SUM,-- 申请区域备货数量合计算 , SUM(a.PRE_SALE_QTY) AS PRE_SALE_QTY_SUM,-- 预估销量合计算 , LISTAGG(a.id,',') WITHIN GROUP(ORDER BY a.id) AS ids FROM STOCK_OPER_APPLY_INFO a join MCMS_BASE_DATA.RP_MATERIAL material ON a.MATERIAL_CODE = material.MATERIAL_CODE WHERE a.material_code IS NOT NULL
AND a.MATERIAL_CODE = #{materialCode}
GROUP BY a.MATERIAL_CODE,a.PLATFORM,a.AREA

